import { useState } from "react";

export default function Semaforo() {
  const [color, setColor] = useState("");

  return (
    <div className="space-y-4">
      <div className="w-24 h-64 bg-gray-800 rounded flex flex-col justify-around items-center p-2">
        {["red", "yellow", "green"].map((c) => (
          <div
            key={c}
            className={`w-16 h-16 rounded-full ${color === c ? c : "bg-gray-500"}`}
          ></div>
        ))}
      </div>
      <div className="space-x-2">
        <button onClick={() => setColor("red")} className="text-red-600">Rojo</button>
        <button onClick={() => setColor("yellow")} className="text-yellow-500">Amarillo</button>
        <button onClick={() => setColor("green")} className="text-green-600">Verde</button>
      </div>
    </div>
  );
}
